export async function seedHabilidades({ notify = true } = {}) {
  // ID interno: apenas letras minúsculas, números, hífens ou underscores
  const PACK_NAME = "habilidades-amaldicoadas";
  const PACK_LABEL = "Habilidades Amaldiçoadas";

  let pack = game.packs.get(`world.${PACK_NAME}`);
  if (!pack) {
    pack = await CompendiumCollection.createCompendium({
      type: "Item",
      label: PACK_LABEL,
      name: PACK_NAME,
      package: "world"
    });
  }

  const wasLocked = pack.locked;
  if (wasLocked) await pack.configure({ locked: false });

  // Limpar compêndio existente para evitar duplicatas (opcional, mas bom para seed)
  const index = await pack.getIndex();
  for (let i of index) {
    await pack.getDocument(i._id).then(d => d.delete());
  }

  // Criar Pastas por Categoria e subpastas por Nível
  const categorias = [...new Set(HABILIDADES_DATA.map(h => h.categoria))];
  const folderMap = {};

  for (let cat of categorias) {
    const parent = await Folder.create({
      name: cat,
      type: "Item",
      folder: null
    }, { pack: pack.collection });
    folderMap[cat] = {};

    const niveis = [...new Set(HABILIDADES_DATA.filter(h => h.categoria === cat).map(h => h.nivel))].sort((a,b) => a - b);
    for (let nivel of niveis) {
      const sub = await Folder.create({
        name: `Nível ${nivel}`,
        type: "Item",
        folder: parent.id
      }, { pack: pack.collection });
      folderMap[cat][nivel] = sub.id;
    }
  }

  // Criar Itens
  const ICON_MAP = {
    "Lutador": "/systems/feiticeiros-e-maldicoes/icons/equipment/hand/glove-leather-red.svg",
    "Especialista em Combate": "/systems/feiticeiros-e-maldicoes/icons/weapons/swords/sword-rapier-basket.svg",
    "Especialista em Técnica": "/systems/feiticeiros-e-maldicoes/icons/weapons/daggers/dagger-guard-gold.svg",
    "Controlador": "/systems/feiticeiros-e-maldicoes/icons/weapons/chains/chain-steel.svg",
    "Suporte": "/systems/feiticeiros-e-maldicoes/icons/weapons/maces/flail-ball-chain.svg"
  };

  const DEFAULT_ICON = "/systems/feiticeiros-e-maldicoes/icons/weapons/axes/axe-battle-red.svg";

  const itemsToCreate = HABILIDADES_DATA.map(h => ({
    name: h.name,
    type: "habilidade",
    img: ICON_MAP[h.categoria] || DEFAULT_ICON,
    folder: folderMap[h.categoria]?.[h.nivel] || null,
    system: {
      descricao: { value: h.description },
      custo: { value: h.system.custo },
      acao: { value: h.system.acao },
      requisito: { value: h.system.requisito },
      fonte: { value: "Livro de Regras v2.5" }
    }
  }));

  await Item.createDocuments(itemsToCreate, { pack: pack.collection });

  if (wasLocked) await pack.configure({ locked: true });
  if (notify) ui.notifications.info(`Compêndio "${PACK_LABEL}" populado com ${itemsToCreate.length} habilidades!`);
  return { reason: 'ok', created: itemsToCreate.length };
}
